function createTableHTML(n){let t="";for(const[e,a]of Object.entries(n))t+=`\n      <tr>\n        <td><code>${escapeHTML(e)}</code></td>\n        <td class="wingdings-char">${escapeHTML(a)}</td>\n      </tr>\n    `;return`\n    <table class="mapping-table">\n      <thead>\n        <tr>\n          <th>文字 (ASCII)</th>\n          <th>記号 (Wingdings)</th>\n        </tr>\n      </thead>\n      <tbody>\n        ${t}\n      </tbody>\n    </table>\n  `}function escapeHTML(n){const t=document.createElement("p");return t.textContent=n,t.innerHTML}document.addEventListener("DOMContentLoaded",async()=>{const n=document.getElementById("mapping-table-container");try{const t=await fetch("../data/wingdings-map.json");if(!t.ok)throw new Error(`HTTP error! status: ${t.status}`);const e=(await t.json()).ascii_to_wingdings;if(!e)throw new Error("Mapping data not found in JSON file.");n.innerHTML=createTableHTML(e)}catch(t){n.innerHTML=`<p class="error">対応表の読み込みに失敗しました: ${t.message}</p>`,console.error("Failed to load mapping table:",t)}});